mapper.select:
SELECT s_id
FROM seat_movie_tbl
WHERE m_code = #{m_movie_cd} AND s_label = #{s_label}

mapper.update:
UPDATE seat_movie_tbl
SET s_is_available = TRUE
WHERE s_id = #{s_id} AND s_is_available = FALSE;

UPDATE seat_movie_tbl
SET s_is_available = FALSE
WHERE s_id = #{s_id} AND s_is_available = TRUE;

mapper.select:
SELECT m_code, s_label
FROM seat_movie_tbl
WHERE s_is_available = 0;
